INSERT INTO `notes`
VALUES (14, NULL, 33, 6,
        '<p>Nie sądzę. Wzrost na 9k jest dosyc duży, zwr&oacute;ć uwage, że cena na dzisiaj to niecałem 7k, także nie spodziewałbym się takiego wzrostu.&nbsp;</p>\r\n',
