INSERT INTO `notes`
VALUES (15, NULL, 33, 7,
        '<p>Tak, ale z tymi wzrostami poczekałbym kilka miesiecy po halvingu. og&oacute;lnie mamy nieciekawą sytuację na rynkach</p>\r\n',
