INSERT INTO `notes`
VALUES (16, NULL, 33, 16,
        '<p>Reset - napewno, ale czy uda się globalnie zamknąć system finansowy? Ja uważam, że nie. Są&nbsp;instytucje/państwa, kt&oacute;re dążą do skumulowania największych zasob&oacute;w (czy to pieniężnych, czy w aktywach, surowcach itp) by być nietykalnym i mieć większą władzę: Rosja gaz, Emiraty ropę, City of London pieniądze i nieruchomości. Oni sobie nie pozwolą na taka standaryzację finansową. Przy okazji polecam zapoznać sie z korporacją City of London, bardzo ciekawe rzeczy się tam dzieją&nbsp;:)</p>\r\n',
        '2020-03-25 10:24:35', '2020-03-25 10:24:35', 0, 2, 0, 0, 0);
