INSERT INTO `notes`
VALUES (253, NULL, 14, 234,
        '<p>Moimi mentorami są osoby, kt&oacute;re wydajemy w instytutkryptografii.pl czyli Phil Konieczny oraz Radek Rygielski. Sami dużo się od nich uczymy. Młodzi są, ale wybitni w tym co robią, geniusze rynk&oacute;w finansowych.&nbsp;</p>\r\n',
