INSERT INTO `notes`
VALUES (254, 247, 14, 226,
        'Niestety, aby i ta koparka się zwróciła uważam, że trzeba mieć wczesny dostęp do sprzętu, zniżki, szybki termin dostawy koparki i dostęp do taniego prądu. Rynek kopania jest bardzo konkurencyjny i rentowność dąży do zera. ',
