INSERT INTO `articles`
VALUES (367, 179, 1, 'Nowa Zelandia wpada w pierwsz recesj od 2010 roku ', '', 1, 0, 1, 0, '2020-06-18 06:17:50',
        '2020-06-18 17:29:38', 'gieldy_kryptowalut.jpg',
